        const fetchData = () => {
            return Axios.post("read-candidate.php?id="+id)
                .then(function (response) {
                    console.log("BIG BOY!!!",response.data[0].votecount);
                    return response.data[0].votecount;
                  })
                  .catch(function (error) {
                    console.log(error);
                 });
            }

        return fetchData;
    }
    function joe(id) {
        console.log("Hi this is mungus and I am " + read(id).then(function(value) {return value[0].votecount}));
        return read(id).then(function(value) {return value[0].votecount})
