            return value[0].name;

        });
    }
    function add(canId) {

        const fetchData = () => {
            return Axios.post("add-one-vote.php", {
                id:canId
                })
                .then(function (response) {
                    console.log(response);
                  })
                  .catch(function (error) {
                    console.log(error);
                 });
            }

        return fetchData;

    }   
    function update_json(id){
        teams = teams.map((team) => {
            if (team._id === id) {
            team.votes = read(id).then(function(value) {
                return value[0].votecount})
            }
            return team;
        });
        setTeams(teams);
    }

