import teamsJson from "../lib/teams.json";
import { Container, Row, Col } from "react-bootstrap";
import VotingCard from "../components/VotingCard.jsx";
import axios from 'axios'

// Importing the Login & Register Componet
import Login from './Login'
import Register from './Register'

// Define the base URL
const Axios = axios.create({
    baseURL: 'http://localhost/php-voting-api/',
});
function Home(){

    const {rootState,logoutUser} = useContext(MyContext);
    const {voteState} = useContext(VoteContext);
    const {isAuth,theUser,showLogin} = rootState;
    let [teams, setTeams] = useState([]);

    useEffect(() => {
      setTeams(teamsJson);
    }, []);

    function incrementVoteCount(teamId) {
        teams = teams.map((team) => {
            if (team._id === teamId) {
            team.votes = team.votes + 1;
            }
            return team;
        });
        setTeams(teams);
    }
    function read(id) {
        try{
            return Axios.get("read-candidate.php?id="+id)
                .then(function(response) {
                    return response.data;
                })
            } catch (error) {
            throw {

                };
            } 
    }   
    console.log("ajofjaoojfa", read(1).then(function(value) { console.log("JARJARBINKA", value[0].votecount)}));
