        read(id).then(function(value) {
            console.log("JEFF GUN" + id, value[0].votecount);
            return value[0].votecount;
        });
    }
    function readName(id) {
        read(id).then(function(value) {
            console.log("JEFF GUN" + id, value[0].name);
            return value[0].name;
        });
    }
    function add(canId) {

        const fetchData = () => {
            return Axios.post("add-one-vote.php", {
                id:canId
                })
                .then(function (response) {
                  })
                  .catch(function (error) {
                    console.log(error);
                 });
            }

        return fetchData;

    }   

    // If user Logged in
    if(isAuth)
    {
        return(
            <Fragment>
                <div className="userInfo">
                    <div className="_img"><span role="img" aria-label="User Image">ðŸ‘¦</span></div>
                    <h1>{theUser.name}</h1>
                    <div className="_email"><span>{theUser.email}</span></div>
                    <button onClick={logoutUser}>Logout</button>
                </div>
                <Container className="app">

                    <div> Yogabab <span>{theUser.name}</span></div>

                    <div> Yogabab <span></span></div>
                    <Row>
                    {teams.map((team) => {
                        return (
                        <Col md={4}>
                            <VotingCard
                            team={team}
                            add={(teamId) => add(teamId)}
                            name={'joe'
                            }
                            votes={readVoteCount(team.id)
                            }
                            />
                        </Col>
                        );
                    })}
                    </Row>
                </Container>
            </Fragment>
        )
    }
    // Showing Login Or Register Page According to the condition
    else if(showLogin){
        return <Login/>;
    }
    else{
        return <Register/>;
    }

}

export default Home;
