import React from "react";
import { Card, Button } from "react-bootstrap";
import axios from 'axios'

// Define the base URL
const Axios = axios.create({
    baseURL: 'http://localhost/php-voting-api/',
});

function VotingCard(props) {
  let { team, add, name, votes, read } = props;

  return (
    <Card style={{ width: "18rem" }}>
      <Card.Img variant="top" src={`/assets/images/${team.logo}`} />
      <Card.Body>
        <Card.Title>{read(team.id).name}</Card.Title>
        <Button variant="success" onClick={add(team.id)}>
          Vote
        </Button>
      </Card.Body>
      <Card.Footer>Vote count: {read(team.id).votecount}</Card.Footer>
    </Card>
  );
}
export default VotingCard;
