import React from "react";
import { Card, Button } from "react-bootstrap";
import axios from 'axios'

// Define the base URL
const Axios = axios.create({
    baseURL: 'http://localhost/php-voting-api/',
});

function VotingCard(props) {
  let { team,voteid, unvoteid, add, votes, subtract, setCand, name, check, addActvButn, getData} = props;
  function vote() {
    add(team.id);
    addActvButn(voteid);
    setCand();
    getData();

  };
  function unVote() {
    subtract(team.id);
    addActvButn(unvoteid);
    setCand();
    getData();
  };
  return (
    <Card style={{ width: "18rem" }}>
      <Card.Img variant="top" src={`/assets/images/${team.picture}`} />
      <Card.Body>
        <Card.Title>{name}</Card.Title>
        <Button id={voteid} variant="success" disabled={check(voteid)} onClick={() => vote()}>
          Vote
        </Button>
        <Button id={unvoteid} variant="success" disabled={check(unvoteid)} onClick={() => unVote()}>
          Unvote
        </Button>

      </Card.Body>
      <Card.Footer>Vote count: {votes}</Card.Footer>
    </Card>
  );
}
export default VotingCard;
