import React from "react";
import { Card, Button } from "react-bootstrap";
import axios from 'axios'

// Define the base URL
const Axios = axios.create({
    baseURL: 'http://localhost/php-voting-api/',
});

function VotingCard(props) {
  let { team, add, unvote, votes, name, picture, disabled, disable} = props;

  return (
    <Card style={{ width: "18rem" }}>
      <Card.Img variant="top" src={`/assets/images/${team.picture}`} />
      <Card.Body>
        <Card.Title>{name}</Card.Title>
        <Button id="vote" variant="success" disabled={disabled} onClick={() => add(team.id), (disabled)=>!disabled}>
          Vote
        </Button>
        <Button id="unvote" variant="success" disabled={disabled} onClick={() => unvote(team.id)}>
          Unvote
        </Button>

      </Card.Body>
      <Card.Footer>Vote count: {team.votecount}</Card.Footer>
    </Card>
  );
}
export default VotingCard;
