import React, { Fragment, useEffect, useState, useContext} from 'react'
import {MyContext} from '../contexts/MyContext'
import teamsJson from "../lib/teams.json";
import { Container, Row, Col } from "react-bootstrap";
import VotingCard from "../components/VotingCard.jsx";

// Importing the Login & Register Componet
import Login from './Login'
import Register from './Register'

function Home(){

    const {rootState,logoutUser} = useContext(MyContext);
    const {isAuth,theUser,showLogin} = rootState;
    let [teams, setTeams] = useState([]);

    useEffect(() => {
      setTeams(teamsJson);
    }, []);

    function incrementVoteCount(teamId) {
      teams = teams.map((team) => {
        if (team._id === teamId) {
          team.votes = team.votes + 1;
        }
        return team;
      });
      setTeams(teams);
    }

    // If user Logged in
    if(isAuth)
    {
        return(
            <Fragment>
                <div className="userInfo">
                    <div className="_img"><span role="img" aria-label="User Image">ðŸ‘¦</span></div>
                    <h1>{theUser.name}</h1>
                    <div className="_email"><span>{theUser.email}</span></div>
                    <button onClick={logoutUser}>Logout</button>
                </div>
                <Container className="app">
                    <Row>
                    {teams.map((team) => {
                        return (
                        <Col md={4}>
                            <VotingCard
                            team={team}
                            addOneVote={(team) => addOneVote(team)}
                            />
                        </Col>
                        );
                    })}
                    </Row>
                </Container>
            </Fragment>
        )
    }
    // Showing Login Or Register Page According to the condition
    else if(showLogin){
        return <Login/>;
    }
    else{
        return <Register/>;
    }

}

export default Home;
