import React, { Fragment, useEffect, useState, useContext} from 'react'
import {MyContext} from '../contexts/MyContext'
import {JoeContext} from './CandData'
import {VoterContext} from './VoterData'

import {VoteContext} from '../contexts/Voting-Context'
import teamsJson from "../lib/teams.json";
import { Container, Row, Col } from "react-bootstrap";
import VotingCard from "../components/VotingCard.jsx";
import axios from 'axios'

// Importing the Login & Register Componet
import Login from './Login'
import Register from './Register'
import CandData from './CandData';

// Define the base URL
const Axios = axios.create({
    baseURL: 'http://localhost/php-voting-api/',
});
function Home(){

    const {rootState,logoutUser} = useContext(MyContext);
    const {obama} = useContext(JoeContext);
    const {joemamba} = useContext(VoterContext);

    const {loading, error, data} = obama;
    const {voterData, voted} = joemamba;

    const {voteState, addVote, readCand} = useContext(VoteContext);
    const {isAuth,theUser,showLogin} = rootState;
    const {cand_data} = voteState;
    let [teams, setTeams] = useState([]);

    useEffect(() => {
      setTeams(teamsJson);
    }, []);

    function incrementVoteCount(teamId) {
        teams = teams.map((team) => {
            if (team._id === teamId) {
            team.votes = team.votes + 1;
            }
            return team;
        });
        setTeams(teams);
    }
    async function read(id) {
        try{
            return Axios.get("read-candidate.php?id="+id)
                .then(function(response) {
                    console.log("READING RAINBOW", response.data[0]);
                    return response.data;
                })
            } catch (error) {
            throw {

                };
            } 
    }   

    function add(canId) {

        const fetchData = () => {
            return Axios.post("add-one-vote.php", {
                id:canId
                })
                .then(function (response) {
                    voted = true;
                    console.log("Hey the vote got added", response);
                  })
                  .catch(function (error) {
                    console.log("ERRORORRRR", error);
                 });
            }
        const insertVoter = () => {
            return Axios.post("insert-voter.php", {
                email:theUser.email,
                voted_for:canId
            }).then(function(response){
                console.log("Voter inserted", voted);
                return(fetchData);

            })
            .catch(function(error) {
                console.log("ERROR INSERT", error, voted);
            })
        }
        const deleteVoter = () => {
            return Axios.post("delete-voter.php", {
                id:theUser.id
            }).then(function(response){
                voted = false;
                console.log("Voter deleted", voted);

            })
            .catch(function(error) {
                console.log("ERROR DELETE", error);
            })
        }
        if (voted) {
            return deleteVoter;
        } else if (!voted) {
            return insertVoter;
        }
    }  

    // If user Logged in
    if(isAuth)
    {
        return(
            <Fragment>
                <div className="userInfo">
                    <div className="_img"><span role="img" aria-label="User Image">ðŸ‘¦</span></div>
                    <h1>{theUser.name}</h1>
                    <div className="_email"><span>{theUser.email}</span></div>
                    <button onClick={logoutUser}>Logout</button>
                </div>
                <Container className="app">

                    <div> Yogaba <span>{data.map((thing) =>thing.picture)}</span></div>
                    <Row>
                    {data.map((thing) => {
                        return (
                        <Col md={4}>
                            <VotingCard
                            team={thing}
                            add={add(thing.id)}
                            votes = {thing.votecount}
                            name = {thing.name}
                            />
                        </Col>
                        );
                    })}
                    </Row>
                </Container>
            </Fragment>
        )
    }
    // Showing Login Or Register Page According to the condition
    else if(showLogin){
        return <Login/>;
    }
    else{
        return <Register/>;
    }

}

export default Home;
