import React from "react";
import axios from "axios";
export const CandDataContext = createContext();

class CandData extends React.Component {
  state = {
    loading: true,
    error: "",
    data: null
  };
  loadData = () => {
    this.setState({ loading: true });
    return axios
      .get(
        `http://localhost/php-voting-api/read-candidate.php/`
      )
      .then(result => {
        console.log("SALUTATIONS", result.data);
        this.setState({
          data: result.data,
          loading: false,
          error: false
        });
      })
      .catch(error => {
        console.error("error: ", error);
        this.setState({
          // objects cannot be used as a react child
          // -> <p>{error}</p> would throw otherwise
          error: `${error}`,
          loading: false
        });
      });
  };
  componentDidMount() {
    this.loadData();
  }
  render() {
    const { loading, error, data } = this.state;
    if (loading) {
      return <p>Loading ...</p>;
    }
    if (error) {
      return (
        <p>
          There was an error loading the repos.{" "}
          <button onClick={this.loadData}>Try again</button>
        </p>
      );
    }
    const contextValue = {
      candRootState:this.state,
    }
    return ( 
    <div>
        <h1>Top Cat Facts</h1>
        {data.map(repo => <p key={repo.id}>name: {repo.name} id: {repo.id} votecount: {repo.votecount}</p>)}
      </div>
    );
  }
}

export default CandData;
