import React, { Fragment, useEffect, useState, useContext} from 'react'
import {MyContext} from '../contexts/MyContext'
import {VoteContext} from '../contexts/Voting-Context'
import teamsJson from "../lib/teams.json";
import { Container, Row, Col } from "react-bootstrap";
import VotingCard from "../components/VotingCard.jsx";
import axios from 'axios'

// Importing the Login & Register Componet
import Login from './Login'
import Register from './Register'

// Define the base URL
const Axios = axios.create({
    baseURL: 'http://localhost/php-voting-api/',
});
function Home(){

    const {rootState,logoutUser} = useContext(MyContext);
    const {addOneVote, readCandidate} = useContext(VoteContext);
    const {isAuth,theUser,showLogin, theCandidate} = rootState;
    let [teams, setTeams] = useState([]);

    useEffect(() => {
      setTeams(teamsJson);
    }, []);
    function read(id) {
        const fetchData = () => {
            return Axios.get("read-candidate.php?id="+id)
                .then(function (response) {
                })
                .catch(function (error) {
                    console.log(error);
                });
            }
        console.log(fetchData);
        return fetchData.response.data[0];
    }      
    function add(canId) {
        const fetchData = () => {
            return Axios.post("add-one-vote.php", {
                id:canId
                })
                .then(function (response) {
                    console.log(response);
                  })
                  .catch(function (error) {
                    console.log(error);
                 });
            }
        return fetchData;
    }   
    function incrementVoteCount(teamId) {
        teams = teams.map((team) => {
            if (team._id === teamId) {
            team.votes = team.votes + 1;
            }
            return team;
        });
        setTeams(teams);
    }

    // If user Logged in
    if(isAuth)
    {
        return(
            <Fragment>
                <div className="userInfo">
                    <div className="_img"><span role="img" aria-label="User Image">👦</span></div>
                    <h1>{theUser.name}</h1>
                    <div className="_email"><span>{theUser.email}</span></div>
                    <button onClick={logoutUser}>Logout</button>
                </div>
                <Container className="app">

                    <div> Yogabab <span>{theUser.name}</span></div>

                    <div> Yogabab <span>{teams.map((teamId) => read(teamId))}</span></div>
                    <Row>
                    {teams.map((team) => {
                        return (
                        <Col md={4}>
                            <VotingCard
                            team={team}
                            add={(teamId) => read(teamId)}
                            joe={(teamId) => read(teamId)}
                            />
                        </Col>
                        );
                    })}
                    </Row>
                </Container>
            </Fragment>
        )
    }
    // Showing Login Or Register Page According to the condition
    else if(showLogin){
        return <Login/>;
    }
    else{
        return <Register/>;
    }

}

export default Home;
