import React from "react";
import { Card, Button } from "react-bootstrap";
import axios from 'axios'

// Define the base URL
const Axios = axios.create({
    baseURL: 'http://localhost/php-voting-api/',
});

function VotingCard(props) {
  let { team,voteid, unvoteid, add, unvote, name, check, addActvButn} = props;

  return (
    <Card style={{ width: "18rem" }}>
      <Card.Img variant="top" src={`/assets/images/${team.picture}`} />
      <Card.Body>
        <Card.Title>{name}</Card.Title>
        <Button id={voteid} variant="success" disabled={check(voteid)} onClick={() => add(team.id), addActvButn(voteid)}>
          Vote
        </Button>
        <Button id={unvoteid} variant="success" disabled={check(unvoteid)} onClick={() => unvote(team.id), addActvButn(unvoteid)}>
          Unvote
        </Button>

      </Card.Body>
      <Card.Footer>Vote count: {team.votecount}</Card.Footer>
    </Card>
  );
}
export default VotingCard;
