import React, { createContext, Component} from "react";
import axios from "axios";
export const CandDataContext = createContext();

class CandData extends Component {
  state = {
    loading: true,
    error: "",
    data: null
  };
  loadData = () => {
    this.setState({ loading: true });
    return axios
      .get(
        `http://localhost/php-voting-api/read-candidate.php/`
      )
      .then(result => {
        console.log("SALUTATIONS", result.data);
        this.setState({
          data: result.data,
          loading: false,
          error: false
        });
      })
      .catch(error => {
        console.error("error: ", error);
        this.setState({
          // objects cannot be used as a react child
          // -> <p>{error}</p> would throw otherwise
          error: `${error}`,
          loading: false
        });
      });
  };
  componentDidMount() {
    this.loadData();
  }
  render() {

    const contextValue= {
      JOE:this.state
    }
    return ( 
      <CandDataContext.Provider value={contextValue}>
        {this.props.children}
      </CandDataContext.Provider>
    );
  }
}

export default CandData;
