import React, { createContext,Component } from "react";
import axios from 'axios'
export const VoteContext = createContext();

// Define the base URL
const Axios = axios.create({
    baseURL: 'http://localhost/php-voting-api/',
});

class VotingContextProvider extends Component{
    constructor(){
        super();
    }
    state = {
        cand_data: [],
    };

    readCand = async (id) => {
        const {response}= await Axios.get("read-candidate.php?id="+id)
        this.setState({cand_data:response.data});
    }   

    addVote(canId) {

        const fetchData = () => {
            return Axios.post("add-one-vote.php", {
                id:canId
                })
                .then(function (response) {
                    console.log(response);
                    return 'lolol';
                  })
                  .catch(function (error) {
                    console.log(error);
                 });
            }

        return fetchData;

    }   
    render(){
        const contextValue = {
            voteState:this.state,
            addVote:this.addVote,
            readCand:this.readCand
        }
        return(
            <VoteContext.Provider value={contextValue}>
                {this.props.children}
            </VoteContext.Provider>
        )
    }
}

export default VotingContextProvider;
